mapper.update:
update product_subcategory
set product_subcategory_name = #{productSubcategoryName}
	, fileDirName = #{fileDirName}
	, filename = #{filename}
	, is_active = #{isActive}
where product_subcategory_id = #{productSubcategoryId}

mapper.select:
select ps.product_subcategory_id, ps.product_subcategory_name, ps.product_topcategory_id, ps.filename, ps.fileDirName, ps.is_active,
pt.product_topcategory_id AS pt_id, pt.product_topcategory_name AS pt_name, pt.fileDirName AS pt_fileDirName, pt.filename AS pt_filename, pt.is_active AS pt_active
from product_subcategory ps join product_topcategory pt on ps.product_topcategory_id = pt.product_topcategory_id where ps.product_subcategory_id = #{productSubCategory}
